package wrapperclasses;

public class Dataconversion_to_string {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a = 10;
         char b = 'r';
         double c = 10.20;
         boolean d = true;
         
         String s = String.valueOf(a);
         System.out.println(s);
         
          s = String.valueOf(b);
         System.out.println(s);
         
         s = String.valueOf(c);
         System.out.println(s);
         
         s = String.valueOf(d);
         System.out.println(s);
	}

}
